<template>
  <div class="file_icon" v-bind:class="calcFormat(format)"></div>
</template>

<script>
export default {
  components: {},

  props: {
    format: null,
  },

  data() {
    return {};
  },

  computed: {},

  methods: {
    calcFormat(type) {
      if (type === "application/pdf") return "pdf";

      if (type === "application/x-zip-compressed") return "zip";
      if (type === "application/x-rar-compressed") return "rar";
      if (type === "application/x-7z-compressed") return "7z";

      if (type === "application/msword") return "doc";
      if (
        type ===
        "application/vnd.openxmlformats-officedocument.wordprocessingml.document"
      )
        return "doc";
      if (type === "application/vnd.ms-excel") return "xls";
      if (
        type ===
        "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"
      )
        return "xls";
      if (type === "application/vnd.ms-powerpoint") return "ppt";
      if (
        type ===
        "application/vnd.openxmlformats-officedocument.presentationml.presentation"
      )
        return "ppt";

      if (type === "image/png") return "img";
      if (type === "image/jpeg") return "img";
      if (type === "image/gif") return "img";
      if (type === "image/svg+xml") return "img";
      // if (type==='image/png') return 'png'
      // if (type==='image/jpeg') return 'jpg'
      // if (type==='image/gif') return 'gif'

      return "default";
    },
  },

  watch: {},

  mounted() {},
};
</script>

